<script>
import { mapGetters } from "vuex";
export default {
  data: function() {
    return {
      Name: "",
      Genre: "",
      Description: ""
    };
  },
  components: {},

  computed: {
    ...mapGetters({
      showModal: "creatplaylist/showModal"
    })
  },
  methods: {
    changeModalState() {
      this.$store.dispatch("creatplaylist/toggleModal");
    },
    ClaimArtistProfile() {
      let payload = {
        name: this.Name,
        genre: this.Genre,
        info: this.Description
      };
      this.$store.dispatch("authorization/ClaimArtistProfile", payload);
    }
  }
};
